import { createContext, useReducer } from "react";
import question from '../data/question_complete.js';

const STAGES = ["Start", "Playing", "End"]

const initialState = {
    gameStage: STAGES[0],
    question,
    currentQuestion: 0,
    score: 0,
    answerSelected: false,
}

const quizReducer  = (state, action) => {

    switch(action.type){
        case "CHANGE_STATE":
            return {
                ...state,
                gameStage: STAGES[1],
            }

            case "REORDER_QUATIONS":
                const reorderedQuestions = question.sort(() => {
                    return Math.random() - 0.5;
                });
              
                return {
                    ...state,
                    question: reorderedQuestions,
                };

            case "CHANGE_QUESTION":
                const nexQuestion = state.currentQuestion + 1;
                let endGame = false

                if(!question[nexQuestion]){
                    endGame = true;
                }

                return{
                    ...state,
                    currentQuestion: nexQuestion,
                    gameStage: endGame ? STAGES[2] : state.gameStage
                }    

                case "NEW_GAME":
                    return initialState;

                case "CHECK_ANSWER":    
                   const answer = action.payload.answer
                   const option = action.payload.option
                   let correctAnswer = 0

                   if(answer === option) correctAnswer = 1;

                   return {
                    ...state,
                    score: state.score + correctAnswer,
                    answerSelected: option,
                   };

            default: 
            return state;
    }

}


export const QuizContext = createContext()

export const QuizProvider = ({children}) => {

    const value = useReducer(quizReducer, initialState);
    return <QuizContext.Provider value={value}>{children}</QuizContext.Provider>;
}
