   const [quizState, ] = useContext(QuizContext);

  return (
    <div id='welcome'>
      <h2>Seja bem-vindo</h2>
      <p>Clique no botão abaixo para começar: </p>
      <button>Iniciar</button>
      <img src={Quiz}></img>
    </div>
  )
}

